,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT',
'  ''f?p=&APP_ID.:'' || cc.summary_page_id || '':'' || :APP_SESSION link,',
'  CASE ',
'    WHEN cc.classification_id = 1 THEN ''Settings''',
'    WHEN cc.classification_id = 2 THEN ''Page/Rpt''',
'    WHEN cc.classification_id = 3 THEN ''SQLi''',
'    WHEN cc.classification_id = 4 THEN ''XSS''',
'    WHEN cc.classification_id = 5 THEN ''URL''',
'  END classification_name,',
'  cc.classification_id,',
'  (c.possible_score - c.score) defects',
'FROM',
'  (',
'  SELECT',
'    classification_id,',
'    SUM(possible_score) possible_score,',
'    SUM(score) score',
'  FROM',
'    (',
'    SELECT',
'      c.classification_id,',
'      COUNT(*) score,',
'      0 possible_score',
'    FROM',
'      sv_sec_collection_data cd,',
'      sv_sec_categories c,',
'      sv_sec_result_temp r',
'    WHERE',
'      c.category_key = cd.category_key',
'      AND cd.result = r.result',
'      AND r.app_session = :APP_SESSION',
'      AND r.app_user = :APP_USER',
'      AND cd.collection_id = SYS_CONTEXT(''SV_SERT_CTX'', ''COLLECTION_ID'')',
'    GROUP BY',
'      c.classification_id',
'    UNION',
'    SELECT',
'      c.classification_id,',
'      0 score,',
'      COUNT(*) possible_score',
'    FROM',
'      sv_sec_collection_data cd,',
'      sv_sec_categories c',
'    WHERE',
'      c.category_key = cd.category_key',
'      AND cd.collection_id = SYS_CONTEXT(''SV_SERT_CTX'', ''COLLECTION_ID'')',
'    GROUP BY',
'      c.classification_id',
'    )',
'  GROUP BY',
'    classification_id',
'  ) c,',
'  sv_sec_classifications cc',
'WHERE',
'  c.classification_id = cc.classification_id',
'  AND c.possible_score - c.score > 0',
'ORDER BY',
'  cc.seq'))
,p_plug_source_type=>'PLUGIN_COM.ORACLE.APEX.D3.BARCHART'
,p_plug_query_row_template=>1
,p_plug_query_options=>'DERIVED_REPORT_COLUMNS'
,p_plug_display_condition_type=>'FUNCTION_BODY'
,p_plug_display_when_condition=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  l_count NUMBER;',
'BEGIN',
'',
'SELECT',
'  COUNT(*)',
'INTO',
'  l_count',
'FROM',
'  (',
'  SELECT',
'    classification_id,',
'    SUM(possible_score) possible_score,',
'    SUM(score) score',
'  FROM',
'    (',
'    SELECT',
'      c.classification_id,',
'      COUNT(*) score,',
'      0 possible_score',
'    FROM',
'      sv_sec_collection_data cd,',
'      sv_sec_categories c,',
'      sv_sec_result_temp r',
'    WHERE',
'      c.category_key = cd.category_key',
'      AND cd.result = r.result',
'      AND r.app_session = :APP_SESSION',
'      AND r.app_user = :APP_USER',
'      AND cd.collection_id = SYS_CONTEXT(''SV_SERT_CTX'', ''COLLECTION_ID'')',
'    GROUP BY',
'      c.classification_id',
'    UNION',
'    SELECT',
'      c.classification_id,',
'      0 score,',
'      COUNT(*) possible_score',
'    FROM',
'      sv_sec_collection_data cd,',
'      sv_sec_categories c',
'    WHERE',
'      c.category_key = cd.category_key',
'      AND cd.collection_id = SYS_CONTEXT(''SV_SERT_CTX'', ''COLLECTION_ID'')',
'    GROUP BY',
'      c.classification_id',
'    )',
'  GROUP BY',
'    classification_id',
'  ) c,',
'  sv_sec_classifications cc',
'WHERE',
'  c.classification_id = cc.classification_id',
'  AND c.possible_score - c.score > 0;',
'',
'',
'IF :APP_PAGE_ID BETWEEN 200 AND 999 AND l_count > 0 THEN',
'  RETURN TRUE;',
'ELSE',
'  RETURN FALSE;',
'END IF;',
'',
'END;'))
