wwv_flow_api.create_page_computation(
 p_id=>wwv_flow_api.id(546187820964552888)
,p_computation_sequence=>10
,p_computation_item=>'P16_CURRENT_VAL'
,p_computation_point=>'BEFORE_HEADER'
,p_computation_type=>'FUNCTION_BODY'
,p_computation=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  l_val                      VARCHAR2(32767);',
'  l_attribute_id             NUMBER;',
'  l_page_id                  NUMBER;',
'  l_component_id             VARCHAR2(1000);',
'  l_column_id                NUMBER;',
'  l_pk                       apex_application_global.vc_arr2;    ',
'BEGIN',
'',
'-- Parse through the p_exception_pk and decompose into individual items',
'l_pk := apex_util.string_to_table(:P16_EXCEPTION_PK,''|'');',
'',
'-- Loop through the table and assign each component',
'-- String should conform to this: ATTRIBUTE_ID:PAGE_ID:COMPONENT_ID:COLUMN_ID',
'FOR x IN 1..l_pk.COUNT',
'LOOP',
'CASE ',
'    WHEN x = 1 THEN NULL;',
'    WHEN x = 2 THEN l_attribute_id := l_pk(x);    ',
'    WHEN x = 3 THEN l_page_id      := l_pk(x);',
'    WHEN x = 4 THEN l_component_id := l_pk(x);',
'    WHEN x = 5 THEN l_column_id    := l_pk(x);',
'  END CASE;',
'END LOOP;',
'',
'SELECT val ',
'  INTO l_val',
'  FROM sv_sec_collection_data',
'  WHERE',
'    collection_id = SYS_CONTEXT(''SV_SERT_CTX'', ''COLLECTION_ID'')',
'    AND attribute_id = l_attribute_id',
'    AND CASE',
'      WHEN  l_page_id IS NOT NULL ',
'        AND l_component_id IS NULL     ',
'        AND l_column_id IS NULL     ',
'        AND page_id = l_page_id THEN 1',
'      WHEN  l_page_id IS NOT NULL ',
'        AND l_component_id IS NOT NULL     ',
'        AND l_column_id IS NULL     ',
'        AND page_id = l_page_id ',
'        AND component_id = l_component_id THEN 1',
'      WHEN  l_page_id IS NOT NULL ',
'        AND l_component_id IS NOT NULL     ',
'        AND l_column_id IS NOT NULL     ',
'        AND page_id = l_page_id',
'        AND component_id = l_component_id',
'        AND column_id = l_column_id THEN 1',
'      ELSE 0',
'    END = 1;',
'',
'RETURN l_val;',
'',
'END;'))
);
